import 'package:flutter/material.dart';import 'package:font_awesome_flutter/font_awesome_flutter.dart';class BottomBar extends StatefulWidget {  dynamic onUpdate;  int selectedIndex;  BottomBar(this.selectedIndex, this.onUpdate);  @override  _BottomBarState createState() => _BottomBarState();}class _BottomBarState extends State<BottomBar> {  @override  Widget build(BuildContext context) {    return Stack(      alignment: Alignment(0.0, -3),      children: [    Column(      mainAxisAlignment: MainAxisAlignment.end,      crossAxisAlignment: CrossAxisAlignment.center,      children: [    bar(),      ],    ),    homeButton(),      ],    );  }  Widget homeButton() {    return Container(        child: Stack(      alignment: Alignment.center,      children: [        Container(          height: 35,          width: 35,          decoration:              BoxDecoration(color: Colors.black54, shape: BoxShape.circle),        ),        InkWell(          onTap: () {            widget.onUpdate(0);          },          child: Container(            height: 30,            width: 30,            decoration: BoxDecoration(                color: widget.selectedIndex == 0                    ? Colors.brown[900]                    : Colors.brown,                shape: BoxShape.circle),            child: Icon(              Icons.home,              color: Colors.white,            ),          ),        ),      ],    ));  }  Widget bar() {    return Container(      height: 50,      decoration: BoxDecoration(          color: Colors.orangeAccent,          borderRadius: BorderRadius.only(            topRight: Radius.circular(20),            topLeft: Radius.circular(20),          )),      child: Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          /*  SizedBox(            height: 10,          ),*/          Row(            mainAxisAlignment: MainAxisAlignment.spaceEvenly,            children: [              InkWell(                onTap: () {                  setState(() {                    widget.onUpdate(1);                  });                },                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.userCircle,                        color: widget.selectedIndex == 1                            ? Colors.brown[900]                            : Colors.brown,                      ),                      Text(                        'PROFILE',                        style: TextStyle(                            fontSize: 7,                            fontWeight: widget.selectedIndex == 1                                ? FontWeight.bold                                : FontWeight.w100,                            color: widget.selectedIndex == 1                                ? Colors.brown[900]                                : Colors.brown),                      ),                    ],                  ),                ),              ),              InkWell(                onTap: () {                  setState(() {                    widget.onUpdate(2);                  });                },                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.calendar,                        color: widget.selectedIndex == 2                            ? Colors.brown[900]                            : Colors.brown,                      ),                      Text(                        'BOOKING',                        style: TextStyle(                            fontSize: 7,                            fontWeight: widget.selectedIndex == 2                                ? FontWeight.bold                                : FontWeight.w100,                            color: widget.selectedIndex == 2                                ? Colors.brown[900]                                : Colors.brown),                      ),                    ],                  ),                ),              ),              SizedBox(                width: 5,              ),              InkWell(                onTap: () {                  setState(() {                    widget.onUpdate(3);                  });                },                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.thumbsUp,                        color: widget.selectedIndex == 3                            ? Colors.brown[900]                            : Colors.brown,                      ),                      Text(                        'LIKE',                        style: TextStyle(                            fontSize: 7,                            fontWeight: widget.selectedIndex == 3                                ? FontWeight.bold                                : FontWeight.w100,                            color: widget.selectedIndex == 3                                ? Colors.brown[900]                                : Colors.brown),                      ),                    ],                  ),                ),              ),              InkWell(                onTap: () {                  setState(() {                    widget.onUpdate(4);                  });                },                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.envelopeOpenText,                        color: widget.selectedIndex == 4                            ? Colors.brown[900]                            : Colors.brown,                      ),                      Text(                        'MESSAGE',                        style: TextStyle(                            fontSize: 7,                            fontWeight: widget.selectedIndex == 4                                ? FontWeight.bold                                : FontWeight.w100,                            color: widget.selectedIndex == 4                                ? Colors.brown[900]                                : Colors.brown),                      ),                    ],                  ),                ),              ),            ],          ),        ],      ),    );  }}