import 'package:flutter/material.dart';import 'package:font_awesome_flutter/font_awesome_flutter.dart';class BottomBar extends StatefulWidget {  @override  _BottomBarState createState() => _BottomBarState();}class _BottomBarState extends State<BottomBar> {int isSelected =0;  @override  Widget build(BuildContext context) {    return Stack(      children: [        Column(          children: [            SizedBox(              height: 615,            ),            Container(              height: 20,              color: Colors.transparent,            ),            bar(),          ],        ),        Positioned(          left: 165,top: 617,          child: homeButton(),        ),      ],    );  }  Widget homeButton() {    return Stack(      children: [        Container(          height: 35,          width: 35,          decoration:              BoxDecoration(color: Colors.black54, shape: BoxShape.circle),        ),        Positioned(            top: 5,            left: 5,            child: InkWell(onTap: (){isSelected=4;},              child: Container(                decoration:                    BoxDecoration(color: isSelected==4? Colors.brown[900]: Colors.blue, shape: BoxShape.circle),                child: Icon(                  Icons.home,                  color: Colors.white,                ),              ),            )),      ],    );  }  Widget bar() {    return Container(      height: 60,      decoration: BoxDecoration(        color: Colors.orangeAccent,        borderRadius: BorderRadius.only(topRight: Radius.circular(20), topLeft: Radius.circular(20), )      ),      child: Column(        children: [          SizedBox(            height: 10,          ),          Row(            mainAxisAlignment: MainAxisAlignment.spaceEvenly,            children: [              InkWell(onTap: (){                setState(() {                  isSelected=0;                });              },                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.userCircle,                        color: Colors.brown[900],                      ),                      Text(                        'PROFILE',                        style: TextStyle(fontSize: 7, color:isSelected==0? Colors.brown[900]:Colors.grey),                      ),                    ],                  ),                ),              ),              InkWell(onTap:(){                setState(() {                  isSelected=1;                });              } ,                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.calendar,                        color: Colors.brown[900],                      ),                      Text(                        'BOOKING',                        style: TextStyle(fontSize: 7, color: isSelected==1 ?Colors.brown[900]: Colors.blue),                      ),                    ],                  ),                ),              ),              SizedBox(width: 5,),              InkWell(onTap: (){                setState(() {                  isSelected=2;                });              },                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.thumbsUp,                        color: Colors.brown[900],                      ),                      Text(                        'LIKE',                        style: TextStyle(fontSize: 7,color:isSelected==2? Colors.brown[900]:Colors.blue),                      ),                    ],                  ),                ),              ),              InkWell(onTap: (){                setState(() {                  isSelected=3;                });              },                child: Container(                  child: Column(                    mainAxisAlignment: MainAxisAlignment.spaceEvenly,                    children: [                      FaIcon(                        FontAwesomeIcons.envelopeOpenText,                        color:isSelected==3? Colors.brown[900]:Colors.blue,                      ),                      Text(                        'PROFILE',                        style: TextStyle(fontSize: 7,color: isSelected==3 ?Colors.brown[900]: Colors.blue),                      ),                    ],                  ),                ),              ),            ],          ),        ],      ),    );  }}