import 'package:flutter/material.dart';import 'package:page_ui/presentation/custom_widget/bottomBar.dart';import 'package:page_ui/presentation/google_map.dart';import 'package:page_ui/presentation/like.dart';import 'package:page_ui/presentation/message.dart';import 'package:page_ui/presentation/profile.dart';import 'bookin.dart';class HomePage extends StatefulWidget {  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  int isSelectedIndex = 0;  @override  void initState() {    super.initState();  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: Center(        child: _getSelectedElement(),      ),      bottomNavigationBar: Container(          height: 50,          decoration: BoxDecoration(            //color: Colors.transparent,              borderRadius: BorderRadius.only(            topRight: Radius.circular(20),            topLeft: Radius.circular(20),          )),          child: BottomBar(isSelectedIndex, _updateIndex)),    );  }  _getSelectedElement() {    switch (isSelectedIndex) {      case 0:        return GoogleMaps();      case 1:        return Profile();      case 2:        return Booking();      case 3:        return Like();      case 4:        return Message();    }  }  _updateIndex(int index) {    setState(() {      isSelectedIndex = index;    });  }}